import json

def find_longest_word(json_string):
    data = json.loads(json_string)
    words = list(data.keys())
    longest_word = max(words, key=len)
    char_counts = {}
    for char in longest_word:
        if char not in char_counts:
            char_counts[char] += 1
        else:
            char_counts[char] = 1
    return (longest_word, char_counts)

