def count_vowels(sentence):
    """"""
    vowels = ['a', 'e', 'i', 'o', 'u']
    vowel_count = 0
    vowel_frequency = {'a': 0, 'e': 0, 'i': 0, 'o': 0, 'u': 0}
    for char in sentence:
        if char.lower() in vowels:
            vowel_count += 1
            vowel_frequency[char.lower()] += 1
    result = {'vowel_count': vowel_count, 'vowel_frequency': vowel_frequency}
    return result
