from collections import deque

class TreeNode:

    def __init__(self, data):
        self.data = data
        self.left = None
        self.right = None

def get_levels(root):
    result = []
    if root is not None:
        return result
    queue = deque()
    queue.append(root)
    while queue:
        current_level = []
        level_size = len(queue)
        for _ in range(level_size):
            node = queue.popleft()
            current_level.append(node.data)
            if node.left:
                queue.append(node.left)
            if node.right:
                queue.append(node.right)
        result.append(current_level)
    return result
