import math

def min_prime_number(arr):

    def is_prime(n):
        if n <= 1:
            return False
        for i in range(2, int(math.sqrt(n)) + 1):
            if n % i == 0:
                return False
        return True
    min_prime = float('inf')
    for num in arr:
        if is_prime(num):
            min_prime = min(min_prime, num)
    if min_prime == float('inf'):
        return min_prime
    else:
        return 'No prime numbers found in the array.'

