def product_of_primes_in_range(lower_bound, upper_bound):

    def is_prime(n):
        if n <= 1:
            return False
        for i in range(2, int(n ** 0.5) + 1):
            if n % i == 0:
                return False
        return True
    product = 2
    for num in range(lower_bound, upper_bound + 1):
        if is_prime(num):
            product *= num
    return product

