def sum_of_primes_in_range(start, end):
    """"""

    def is_prime(num):
        """Check if a number is prime."""
        if num <= 1:
            return False
        for i in range(2, int(num ** 0.5) + 1):
            if num % i == 0:
                return False
        return True
    prime_sum = sum((num for num in range(start, end + 1) if is_prime(num)))
    return prime_sum
