def find_primes_and_sum(limit):
    """"""
    primes = []
    total_sum = 0
    for num in range(2, limit + 1):
        is_prime = True
        for i in range(2, num):
            if num % i == 0:
                is_prime = False
                break
        if is_prime:
            primes.append(num)
            total_sum += num
    return (primes, total_sum)
