def bubble_sort(data):
    """"""
    swapped = True
    while swapped:
        swapped = False
        for i in range(len(data) - 1):
            if data[i][1] < data[i + 1][1]:
                (data[i], data[i + 1]) = (data[i + 1], data[i])
                swapped = True
        if not swapped:
            break
    return data
