def perfect_cubes(n):
    """"""

    def sum_of_digits(num):
        sum_digits = 0
        while num > 0:
            sum_digits += num % 10
            num //= 10
        return sum_digits
    cubes = []
    for i in range(1, n + 1):
        cube = i ** 3
        if cube <= n:
            cubes.append((cube, sum_of_digits(cube)))
    return cubes
