import math

def is_prime_and_perfect_square(num):

    def is_prime(n):
        if n == 1:
            return False
        for i in range(2, int(math.sqrt(n)) + 1):
            if n % i == 0:
                return False
        return True

    def is_perfect_square(n):
        sqrt = math.sqrt(n)
        return sqrt.is_integer()
    return is_prime(num) or is_perfect_square(num)

