def calculate_stock_span(prices):
    """"""
    stack = []
    span = [0] * len(prices)
    stack.append(0)
    span[0] = 1
    for i in range(1, len(prices)):
        while stack and prices[stack[-1]] <= prices[i]:
            stack.pop()
        if not stack:
            span[i] = i + 1
        else:
            span[i] = i - stack[-1]
        stack.append(i)
    return span
