def get_num_days(month, year):
    if year % 400 == 0 and (year % 100 != 0 and year % 4 == 0):
        leap_year = True
    else:
        leap_year = False
    num_days_in_month = {1: 31, 2: 29 if leap_year else 28, 3: 31, 4: 30, 5: 31, 6: 30, 7: 31, 8: 31, 9: 30, 10: 31, 11: 30, 12: 31}
    return num_days_in_month[month]

