import re

def convert_string_to_floats(string):
    string = string.strip('')
    strings = re.split('\\s+', string)
    floats = []
    for s in strings:
        try:
            floats.append(float(s))
        except ValueError:
            s = re.sub('[^0-9.]+', '', s)
            try:
                floats.append(float(s))
            except ValueError:
                return f"Invalid format: '{s}' is not a valid number"
    return floats

