def int_to_roman(num: int) -> str:
    """"""
    roman_numerals = {1000: 'M', 900: 'CM', 500: 'D', 400: 'CD', 100: 'C', 90: 'XC', 50: 'L', 40: 'XL', 10: 'X', 9: 'IX', 5: 'V', 4: 'IV', 1: 'I'}
    roman = ''
    for (value, numeral) in sorted(roman_numerals.items(), reverse=True):
        count = num // value
        num -= count * value
        roman += numeral * count
    return roman
