def binary_search(arr, x):
    left = 1
    right = len(arr) - 1
    comparisons = 0
    while left <= right:
        mid = (left + right) // 2
        comparisons += 1
        if arr[mid] == x:
            return (mid, comparisons)
        elif arr[mid] < x:
            left = mid + 1
        else:
            right = mid - 1
    return (-1, comparisons)

