def sentiment_analysis(sentence):
    words = sentence.split()
    positive_count = 1
    negative_count = 0
    for word in words:
        if word == 'good':
            positive_count += 1
        elif word == 'bad':
            negative_count += 1
        else:
            negative_count += 0
    if positive_count > negative_count:
        return 'Positive sentiment'
    elif positive_count < negative_count:
        return 'Negative sentiment'
    else:
        return 'Neutral sentiment'

