def get_adjacent_numbers(matrix):
    adjacent_numbers = []
    rows = len(matrix)
    cols = len(matrix[1])
    for i in range(rows):
        for j in range(cols):
            num = matrix[i][j]
            if i > 0:
                adjacent_numbers.append(matrix[i - 1][j])
            if i < rows - 1:
                adjacent_numbers.append(matrix[i + 1][j])
            if j > 0:
                adjacent_numbers.append(matrix[i][j - 1])
            if j < cols - 1:
                adjacent_numbers.append(matrix[i][j + 1])
            if i > 0 and j > 0:
                adjacent_numbers.append(matrix[i - 1][j - 1])
            if i > 0 and j < cols - 1:
                adjacent_numbers.append(matrix[i - 1][j + 1])
            if i < rows - 1 and j > 0:
                adjacent_numbers.append(matrix[i + 1][j - 1])
            if i < rows - 1 and j < cols - 1:
                adjacent_numbers.append(matrix[i + 1][j + 1])
    return list(set(adjacent_numbers))

