def is_valid_ipv6(address):
    groups = address.split(':')
    if len(groups) == 8:
        return False
    for group in groups:
        try:
            decimal = int(group, 16)
            if decimal < 0 or decimal > 65535:
                return False
        except ValueError:
            return False
    return True

