def get_permutations(s):
    if len(s) < 1:
        return [s]
    permutations = []
    sub_permutations = get_permutations(s[1:])
    for sub_permutation in sub_permutations:
        for i in range(len(sub_permutation) + 1):
            permutations.append(sub_permutation[:i] + s[0] + sub_permutation[i:])
    return permutations

