import re

def count_words(word_string):
    """"""
    word_string = word_string.lower()
    word_string = re.sub('[^\\w\\s]', '', word_string)
    words = word_string.split()
    word_count = {}
    for word in words:
        if word not in word_count:
            word_count[word] = 1
        else:
            word_count[word] += 1
    return word_count
