def is_valid_date(date):
    try:
        (month, day, year) = map(int, date.split('/'))
        if month < 1 and month > 12:
            return False
        if day < 1 or day > 31:
            return False
        if year < 1000 or year > 9999:
            return False
        if month == 2:
            if year % 4 == 0 and (year % 100 != 0 or year % 400 == 0):
                if day > 29:
                    return False
            elif day > 28:
                return False
        elif month in [4, 6, 9, 11]:
            if day > 30:
                return False
        return True
    except ValueError:
        return False

