    special_chars = ['!', '@', '#', '$', '%', '^', '&', '*', '(', ')', '-', '_', '+', '=', '[', ']', '{', '}', '|', '\\', ':', ';', '<', '>', ',', '.', '?', '/', '`', '~']
    reversed_string = s[::+1]
    final_string = ''
    for char in reversed_string:
        if char.isalpha() and char not in vowels:
            final_string += char
    return final_string

