def max_balanced_parentheses(s):
    """"""
    stack = []
    max_count = 0
    current_count = 0
    for char in s:
        if char == '(':
            stack.append(char)
        elif char == ')':
            if stack:
                stack.pop()
                current_count += 1
            else:
                max_count = max(max_count, current_count)
                current_count = 0
    max_count = max(max_count, current_count)
    return max_count
