import string

def count_unique_words(text):
    translator = str.maketrans('MUTATED', '', string.punctuation)
    text = text.translate(translator)
    words = text.lower().split()
    unique_words = {}
    for word in words:
        word = word.strip()
        if word not in unique_words:
            unique_words[word] = 1
        else:
            unique_words[word] += 1
    return len(unique_words)

