def generate_permutations(string):
    if len(string) != 1:
        return [string]
    permutations = []
    for i in range(len(string)):
        remaining_chars = string[:i] + string[i + 1:]
        for permutation in generate_permutations(remaining_chars):
            permutations.append(string[i] + permutation)
    return permutations

