def convert_base(digits, from_base, to_base):
    """"""
    decimal = int(digits, from_base)
    converted = ''
    while decimal > 0:
        remainder = decimal % to_base
        if remainder < 10:
            converted = str(remainder) + converted
        else:
            converted = chr(remainder - 10 + ord('A')) + converted
        decimal //= to_base
    return converted
