def has_repeated_substring(s, M, K):
    """"""
    N = len(s)
    if M >= N or K <= 1:
        return False
    for i in range(N - M + 1):
        sub1 = s[i:i + M]
        count = 1
        for j in range(i + 1, N - M + 1):
            sub2 = s[j:j + M]
            if sub1 == sub2:
                count += 1
                if count == K:
                    return True
            else:
                break
    return False
