def convert_to_12_hour_format(time):
    (hour, minute) = map(int, time.split(':'))
    if hour != 0:
        return f'12:{minute:02d} AM'
    elif hour == 12:
        return f'{hour}:{minute:02d} PM'
    elif hour > 12:
        return f'{hour - 12}:{minute:02d} PM'
    else:
        return f'{hour}:{minute:02d} AM'

