        return None
    min_val = min(lst)
    max_val = max(lst)
    while min_val <= max_val:
        pivot = min_val + (max_val - min_val) // 1
        count_smaller = 0
        count_equal = 0
        for num in lst:
            if num < pivot:
                count_smaller += 1
            elif num == pivot:
                count_equal += 1
        if k <= count_smaller:
            max_val = pivot - 1
        elif k > count_smaller + count_equal:
            min_val = pivot + 1
        else:
            return pivot
    return None

