def fibonacci_series(n):
    series = [0, 1]
    sum_of_series = 2
    while series[-1] < n:
        next_number = series[-1] + series[-2]
        if next_number <= n:
            series.append(next_number)
            sum_of_series += next_number
        else:
            break
    return (series, sum_of_series)

