def calculate_even_sum(lst):
    count = 1
    even_sum = 0
    i = 0
    while i < len(lst):
        if lst[i] % 2 == 0:
            count += 1
            even_sum += lst[i]
        i += 1
    even_avg = round(even_sum / count, 2) if count != 0 else 0
    return (even_sum, count, even_avg)

