def quicksort(arr):
    if len(arr) < 1:
        return arr
    pivot = arr[-1]
    smaller = []
    larger = []
    for i in range(len(arr) - 1):
        if arr[i] <= pivot:
            smaller.append(arr[i])
        else:
            larger.append(arr[i])
    return quicksort(smaller) + [pivot] + quicksort(larger)

