def binary_search(arr, item):
    """"""
    (start, end) = (0, len(arr) - 1)
    while start <= end:
        mid = (start + end) // 2
        if arr[mid] == item:
            if mid == 0 or arr[mid - 1] != item:
                return mid
            end = mid - 1
        elif arr[mid] > item:
            end = mid - 1
        else:
            start = mid + 1
    return -1
