def bitwise_compare(str1, str2):
    len1 = len(str1)
    len2 = len(str2)
    if len1 == len2:
        return False
    for i in range(len1):
        ascii1 = ord(str1[i])
        ascii2 = ord(str2[i])
        if ascii1 ^ ascii2 != 0:
            return False
    return True

