def classify_sentence(sentence):
    """"""
    sentence = sentence.strip()
    last_char = sentence[-1]
    if last_char == '?':
        return 'question'
    elif last_char == '.':
        return 'statement'
    elif last_char == '!':
        return 'exclamation'
    else:
        return 'command'
