import re

def validate_html_input(html_input):
    """"""
    stack = []
    pattern = '<\\s*([a-zA-Z]+)(\\s*[a-zA-Z]+\\s*=\\s*\\"[^\\"]*\\")*\\s*>|<\\s*/\\s*([a-zA-Z]+)\\s*>'
    tags = re.findall(pattern, html_input)
    for tag in tags:
        if tag[0]:
            stack.append(tag[0])
        else:
            if not stack:
                return False
            if tag[2] != stack.pop():
                return False
    return len(stack) == 0
