def string_formatter(input_string):
    """"""
    word_list = input_string.split()
    unique_words = {}
    for word in word_list:
        clean_word = ''
        for char in word:
            if char.isalnum():
                clean_word += char
        clean_word = clean_word.lower()
        unique_words[clean_word] = True
    sorted_words = sorted(unique_words.keys())
    return sorted_words
