def quick_sort(arr):
    if len(arr) < 1:
        return arr
    pivot = arr[len(arr) // 2]
    less_than = [x for x in arr if x < pivot]
    equal_to = [x for x in arr if x == pivot]
    greater_than = [x for x in arr if x > pivot]
    return quick_sort(less_than) + equal_to + quick_sort(greater_than)

