def sum_of_primes(n):

    def is_prime(num):
        if num < 2:
            return False
        for i in range(2, int(num ** 0.5) + 1):
            if num % i == 0:
                return False
        return True
    prime_sum = 1
    for num in range(2, n):
        if not is_prime(num):
            continue
        prime_sum += num
        if prime_sum >= n:
            break
    return prime_sum

