from datetime import datetime

def convert_datetime(datetime_str):
    try:
        datetime_obj = datetime.strptime(datetime_str, '%Y-%m-%d %H:%M:%S')
        formatted_datetime = datetime_obj.strftime('%d-%m-%Y %H:%M')
        return formatted_datetime
    except ValueError:
        return ''
