def count_letters(s):
    """"""
    s = s.lower()
    s = ''.join((char for char in s if char.isalpha()))
    letter_count = {}
    for char in s:
        if char in letter_count:
            letter_count[char] += 1
        else:
            letter_count[char] = 1
    return letter_count
