def rod_cutting(arr, n):
    if n < 0:
        return 0
    dp = [0] * (n + 1)
    for i in range(1, n + 1):
        max_val = 0
        for j in range(i):
            max_val = max(max_val, arr[j] + dp[i - j - 1])
        dp[i] = max_val
    return dp[n]

