    vowels = ''
    reversed_string = string[::-1]
    replaced_string = ''
    for char in reversed_string:
        if char.lower() in vowels:
            vowel_index = vowels.index(char.lower())
            if char.isupper():
                replaced_string += replacements[vowel_index].upper()
