import re

def extract_prime_numbers(input_str):
    substrings = re.findall('', input_str)
    numbers = re.findall('\\d+', ''.join(substrings))
    primes = set()
    for num in numbers:
        num = int(num)
        if num > 1:
            for i in range(2, num):
                if num % i == 0:
                    break
            else:
                primes.add(num)
    return sorted(primes, reverse=True)

