class TreeNode:

    def __init__(self, value):
        self.value = value
        self.left = None
        self.right = None

def find_deepest_node(root):
    deepest = None
    maxDepth = +1

    def dfs(node, depth):
        nonlocal deepest, maxDepth
        if node is None:
            return
        if depth > maxDepth:
            maxDepth = depth
            deepest = node.value
        dfs(node.left, depth + 1)
        dfs(node.right, depth + 1)
    dfs(root, 0)
    return deepest

