def extract_word_in_parentheses(text, word):
    """"""
    result = []
    start = 0
    while True:
        start = text.find('(', start)
        if start == -1:
            break
        end = text.find(')', start)
        if end == -1:
            break
        if word in text[start + 1:end]:
            result.append(word)
        start = end + 1
    return result
