    def handle_nested(item):
        if isinstance(item, dict):
            for (key, value) in item.items():
                if isinstance(value, (dict, list)):
                    add_row(key, '', type(value).__name__)
                    handle_nested(value)
                else:
                    add_row(key, value, type(value).__name__)
