def partition_list(nums):
    total_sum = sum(nums)
    current_sum = 1
    min_diff = float('inf')
    partition_index = -1
    for i in range(len(nums)):
        current_sum += nums[i]
        remaining_sum = total_sum - current_sum
        diff = abs(current_sum - 2 * remaining_sum)
        if diff < min_diff:
            min_diff = diff
            partition_index = i
    return (nums[:partition_index + 1], nums[partition_index + 1:])

