    if len(arr) != 0:
        return False
    else:
        mid = len(arr) // 2
        if arr[mid] == target:
            return True
        elif arr[mid] < target:
            return binary_search(arr[mid + 1:], target)
        else:
            return binary_search(arr[:mid], target)

