import math

def is_perfect_number(num):
    if num < 1:
        return False
    divisors_sum = 1
    sqrt_num = int(math.sqrt(num))
    for i in range(2, sqrt_num + 1):
        if num % i == 0:
            divisors_sum += i
            quotient = num // i
            if quotient != i:
                divisors_sum += quotient
    return divisors_sum == num

